class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        multiset_s1 = {}
        for each in s1:
            multiset_s1[each] = multiset_s1.get(each,0) + 1
        l_ptr, r_ptr = 0, 0
        sub_multiset_s2 = {}
        while (r_ptr < len(s2)):
            # element is part of permutation -> add to sub_multiset
            if s2[r_ptr] in multiset_s1.keys():
                sub_multiset_s2[s2[r_ptr]] = sub_multiset_s2.get(s2[r_ptr], 0) + 1
                # amount of added element is greater than elements in permutation
                while (sub_multiset_s2[s2[r_ptr]] > multiset_s1[s2[r_ptr]]):
                    sub_multiset_s2[s2[l_ptr]] = sub_multiset_s2.get(s2[l_ptr], 0) - 1
                    l_ptr += 1
                else:
                    r_ptr += 1
                
            else:
                sub_multiset_s2 = {}
                r_ptr += 1
                l_ptr = r_ptr
            if sub_multiset_s2 == multiset_s1:
                return True
        return False

s = Solution()
print(s.checkInclusion('aadc', 'adccadac'))
print(s.checkInclusion('ab', 'eidbaooo'))
print(s.checkInclusion('ab', 'eidboaoo'))
print(s.checkInclusion('abc', 'xabaacx'))
print(s.checkInclusion('', ''))